export const addItemToCart = (cartItems, cartItemsToAdd) => {
 const existingCartItem = cartItems.find(cartItem => cartItem.id === cartItemsToAdd.id)

 if(existingCartItem) {
  return cartItems.map(cartItem => cartItem.id === cartItemsToAdd.id ? {...cartItem, quantity: cartItem.quantity + 1} : cartItem)
 }

 return [...cartItems, {...cartItemsToAdd, quantity: 1}]
}

export const removeItemFromCart = (cartItems, cartItemsToDemove) => {
 const existingCartItem = cartItems.find(cartItem => cartItem.id === cartItemsToDemove.id);

 if(existingCartItem) {
  return cartItems.filter(cartItem => cartItem.id !== cartItemsToDemove.id)
 }
}
